INSERT INTO `forum_posts` VALUES ('166', 'forum_46654fa5be431', '146', '2007-06-05 16:49:37', 'mpf2', 'Re: Forum di benvenuto', 'Buongiorno, sono un\'insegnante di scuola primaria...recentemente nella mia classe è arrivata una bambina con problemi, a mio parere da non sottovalutare. Praticamente la bimba simula l\'atto sessuale in classe. Ho parlato con lei e ho rilevato che spesso rimane sola davanti al televisore...mi piacerebbe poterla aiutare. Il vostro corso può essermi d\'aiuto?<br /><br /><br /><b>---- (mpf3) Gruppo Tre Mapa ha scritto:</b><br /><p>Benvenuti! Questo spazio è per voi.........</p><br /><b>--------------------------------</b> <br />', '', '0', '', '2007-06-05 16:49:37', '', 'messaggio', null, 'corsotre', 'ed1', '', null, null);
