INSERT INTO `forum_posts` VALUES ('172', 'forum_46654fa5be431', '0', '2007-06-05 16:58:10', 'mpf3', 'caro fleo', '<p>Gentile amico,</p><p>la questione da te posta ci è parsa subito interessante e pertinente; d\'altro canto avrai tu stesso modo di conststsrae come tutti i moduli previsti siano infondo tenuti insieme da un presupposto fondamentale: la televisione ed i suoi contenuti, spot compresi, inevitabilmente producono riflessi e significativi effetti sulla psiche ancora in divenire dei piccoli spettatori.</p><p>Siamo felici di poter contare sul tuo contributo prezioso. Grazie e a presto.</p>', '', '0', '', '2007-06-05 16:58:10', '', 'messaggio', null, 'corsotre', 'ed1', '', null, null);
